Ext.define('MyApp.view.MyViewport', {
    extend: 'Ext.container.Viewport',

    layout: {
        type: 'fit'
    },

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'panel',
                    id: 'vbox',
                    autoScroll: true,
                    layout: {
                        align: 'stretch',
                        type: 'vbox'
                    },
                    title: 'My Panel',
                    listeners: {
                        add: {
                            fn: me.onVboxAdd,
                            scope: me
                        }
                    }
                }
            ]
        });

        me.callParent(arguments);
    },

    onVboxAdd: function(container, component, index, eOpts) {
        var editor = new Ext.Editor({
            updateEl: true, // update the innerHTML of the bound element when editing completes
            field: {
                xtype: 'combobox'
            }
        });

        if (!this.t) {


            this.t=Ext.Function.createBuffered(function(){
                Ext.Function.defer(function(){Ext.each(Ext.query('.tutu'), function(el){
                    console.log(el)
                    Ext.get(el).on('click',function(){
                        editor.startEdit(this)
                    });
                })}, 1000);
            }, 5)
        }

        this.t();

    }

});